DROP DATABASE IF EXISTS `xzqa`;
CREATE DATABASE `xzqa` DEFAULT CHARACTER SET utf8;
USE `xzqa`;

-- 文章分类表
CREATE TABLE `xzqa_category`(
  `id` SMALLINT PRIMARY KEY AUTO_INCREMENT,
  `category_name` VARCHAR(30)
);

-- 文章作者表(注册用户)
CREATE TABLE `xzqa_author`(
  `id` INT PRIMARY KEY AUTO_INCREMENT,
  `username` VARCHAR(30) NOT NULL UNIQUE,
  `password` VARCHAR(32) NOT NULL,
  `nickname` VARCHAR(30),
  `avator` VARCHAR(50),
  `article_number` MEDIUMINT NOT NULL DEFAULT 0
);

-- 文章表
CREATE TABLE `xzqa_article`(
  `id` INT PRIMARY KEY AUTO_INCREMENT,
  `subject` VARCHAR(50) NOT NULL,
  `description` VARCHAR(255),
  `content` MEDIUMTEXT,
  `image` VARCHAR(100),
  `category_id` SMALLINT,
  `author_id` INT,
  CONSTRAINT `fk_category_id` FOREIGN KEY(`category_id`) REFERENCES `xzqa_category`(`id`),
  CONSTRAINT `fk_author_id` FOREIGN KEY(`author_id`)  REFERENCES `xzqa_author`(`id`),
  `created_at` INT
);

